import * as React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styled from 'styled-components';
import { useAppDispatch } from '../../helpers/hooks';
import { commonActions } from '../../store/slices/common';
import {
  useAllRaces,
  useAllSubRaces,
  useAllClasses,
  useAllSubClasses,
} from '../../store/selectors/common';
import { TEXT_COLOR_PRIMARY, TEXT_COLOR_SECONDARY } from '../styles';

const StyledCharacterBuilderContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  color: ${TEXT_COLOR_PRIMARY};
`;

const StyledCharacterBuilderTitle = styled.h1`
  text-align: center;
`;

const StyledCharacterBuilderTitleContainer = styled.div`
  text-align: center;
  margin-bottom: 32px;
`;

const StyledStepsContainer = styled.div``;

const StyledStep = styled.div``;

const StyledStepsHeader = styled.h3`
  text-align: center;
  margin: 12px 0;
`;

const StyledStepsSubheader = styled.h5`
  text-align: center;
  margin: 12px 0;
`;

const StyledSelect = styled.select`
  width: 200px;
  height: 30px;
  border-radius: 5px;
  margin: 12px 0;
`;

const StyledRandomiseButton = styled.a`
  font-size: inherit;
  color: ${TEXT_COLOR_SECONDARY};
  &:hover {
    cursor: pointer;
    color: ${TEXT_COLOR_PRIMARY};
  }
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const StyledP = styled.p`
  margin: 12px 0;
`;

interface FormInputs {
  race: string;
  subRace: string;
  class: string;
  subClass: string;
}

export const CharacterBuilder: React.FC = () => {
  const dispatch = useAppDispatch();
  const [displayRandom, setDisplayRandom] = React.useState(false);
  const allRaces = useAllRaces();
  const allSubRaces = useAllSubRaces();
  const allClasses = useAllClasses();
  const allSubClasses = useAllSubClasses();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<FormInputs>();
  const onSubmit: SubmitHandler<FormInputs> = (data) => console.log(data);
  const [selectedRace, setSelectedRace] = React.useState<string>('');
  const [selectedSubRace, setSelectedSubRace] = React.useState<string>('');
  const [selectedClass, setSelectedClass] = React.useState<string>('');
  const [selectedSubClass, setSelectedSubClass] = React.useState<string>('');

  const getRandomInt = (maxInt: number) => {
    return Math.floor(Math.random() * maxInt);
  };

  const handleRandomise = (arr: Array<any>) => {
    const randomInt = getRandomInt(arr.length);
    return arr[randomInt];
  };

  const handleRandomRace = () => {
    const randomRace = handleRandomise(allRaces);
    setSelectedRace(randomRace.name);
  };

  React.useEffect(() => {
    dispatch(commonActions.getAllRaces());
    if (selectedRace) {
      dispatch(commonActions.getAllSubRaces());
    }
    if (selectedSubClass || !allSubRaces || allSubRaces.length === 0) {
      dispatch(commonActions.getAllClasses());
    }
  }, [dispatch, selectedRace]);

  return (
    <StyledCharacterBuilderContainer>
      <StyledCharacterBuilderTitleContainer>
        <StyledCharacterBuilderTitle>
          Character builder
        </StyledCharacterBuilderTitle>
        <StyledP>
          Build your character step by step or completely{' '}
          <StyledRandomiseButton>randomise</StyledRandomiseButton>.
        </StyledP>
      </StyledCharacterBuilderTitleContainer>
      {!displayRandom ? (
        <StyledStepsContainer>
          <StyledForm onSubmit={handleSubmit(onSubmit)}>
            {/* Select a race */}
            <StyledStep>
              <StyledStepsHeader>Step One: Race</StyledStepsHeader>
              <StyledStepsSubheader>
                Select a race or{' '}
                <StyledRandomiseButton onClick={() => handleRandomRace()}>
                  randomise
                </StyledRandomiseButton>{' '}
                it.
              </StyledStepsSubheader>
              <StyledSelect
                value={selectedRace}
                {...register('race')}
                onChange={(e) => {
                  setSelectedRace(e.target.value);
                }}
              >
                <option value="" selected disabled>
                  - - - -
                </option>
                {allRaces &&
                  allRaces.map((race) => (
                    <option key={race.index}>{race.name}</option>
                  ))}
              </StyledSelect>

              {errors.race && <span>This field is required</span>}
            </StyledStep>
            {/* Select a subrace */}
            {allSubRaces && (
              <StyledStep>
                <StyledStepsSubheader>
                  Select a subrace or{' '}
                  <StyledRandomiseButton onClick={() => handleRandomRace()}>
                    randomise
                  </StyledRandomiseButton>{' '}
                  it.
                </StyledStepsSubheader>
                <StyledSelect
                  value={selectedSubRace}
                  {...register('subRace')}
                  onChange={(e) => {
                    setSelectedRace(e.target.value);
                  }}
                >
                  <option value="" selected disabled>
                    - - - -
                  </option>
                  {allSubRaces &&
                    allSubRaces.map((race) => (
                      <option key={race.index}>{race.name}</option>
                    ))}
                </StyledSelect>

                {errors.subRace && <span>This field is required</span>}
              </StyledStep>
            )}
            {/* Select a class */}
            {/* Select a subclass */}
            {/* Set ability scores */}
            {/* Select a background */}
          </StyledForm>
        </StyledStepsContainer>
      ) : (
        <h2>Random</h2>
      )}
    </StyledCharacterBuilderContainer>
  );
};
