import * as React from 'react';
import {
  StyledContainer,
  StyledRadio,
  StyledStepsHeader,
  StyledStepsSubheader,
  StyledRandomiseButton,
  StyledStepContainer,
  StyledButtonContainer,
  StyledStepButton,
  StyledLabel,
} from '../styles';
import { GenericComponentProps } from '../../types';

export const Background: React.FC<GenericComponentProps> = (props) => {
  const parentsKnowledge = props.getFieldValue('background.parents.knowledge');
  const handleRandomBackground = () => {
    // do something random
  };

  return (
    <StyledStepContainer>
      <StyledContainer>
        <StyledStepsHeader>Step Four: Background</StyledStepsHeader>
        <StyledStepsSubheader>
          Build as much of a background as you'd like, or completely{' '}
          <StyledRandomiseButton onClick={() => handleRandomBackground()}>
            randomise
          </StyledRandomiseButton>{' '}
          it.
        </StyledStepsSubheader>
        {/* Parents */}
        <StyledStepsSubheader>
          <strong>Parents</strong>
        </StyledStepsSubheader>
        <StyledLabel>Known</StyledLabel>
        <StyledRadio
          type="radio"
          value="known"
          {...props.register('background.parents.knowledge')}
        />

        <StyledLabel>Unknown</StyledLabel>
        <StyledRadio
          type="radio"
          value="unknown"
          {...props.register('background.parents.knowledge')}
        />
        {/* Birthplace */}
        {/* Siblings */}
        <StyledButtonContainer>
          <StyledStepButton onClick={props.handleStepBack}>
            &#8592; Previous: Ability scores
          </StyledStepButton>
          <StyledStepButton onClick={props.handleStepForward}>
            Next: ? &#8594;
          </StyledStepButton>
        </StyledButtonContainer>
      </StyledContainer>
    </StyledStepContainer>
  );
};
