import * as React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styled from 'styled-components';
import { useAppDispatch } from '../../helpers/hooks';
import { commonActions } from '../../store/slices/common';
import { useAllRaces } from '../../store/selectors/common';
import { TEXT_COLOR_PRIMARY } from '../styles';

const StyledCharacterBuilderContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  color: ${TEXT_COLOR_PRIMARY};
`;

const StyledCharacterBuilderTitle = styled.h1`
  text-align: center;
  margin-bottom: 32px;
`;

const StyledStepsContainer = styled.div``;

const StyledStepsHeader = styled.h2`
  text-align: center;
`;

const StyledStepsSubheader = styled.h4`
  text-align: center;
`;

const StyledSelect = styled.select`
  width: 200px;
  height: 30px;
  border-radius: 5px;
`;

const StyledRandomiseButton = styled.a`
  font-size: inherit;
  text-decoration: underline;
  color: ${TEXT_COLOR_PRIMARY};
  &:hover {
    cursor: pointer;
  }
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

type Inputs = {
  race: string;
  raceRequired: string;
};

// When page loads, animate into character builder component
// When page loads, make a request to the API to get back all races

export const CharacterBuilder: React.FC = () => {
  const dispatch = useAppDispatch();
  const allRaces = useAllRaces();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<Inputs>();
  const onSubmit: SubmitHandler<Inputs> = (data) => console.log(data);
  const [selectedRace, setSelectedRace] = React.useState<string>('');

  React.useEffect(() => {
    dispatch(commonActions.getAllRaces());
    console.log(allRaces);
  }, [dispatch]);

  return (
    <StyledCharacterBuilderContainer>
      <StyledCharacterBuilderTitle>
        Character builder
      </StyledCharacterBuilderTitle>
      <StyledStepsContainer>
        <StyledStepsHeader>Step One: Race</StyledStepsHeader>
        <StyledStepsSubheader>
          Select a race or{' '}
          <StyledRandomiseButton>randomise</StyledRandomiseButton>
        </StyledStepsSubheader>
        <StyledForm onSubmit={handleSubmit(onSubmit)}>
          {/* register your input into the hook by invoking the "register" function */}
          <StyledSelect
            defaultValue="test"
            {...register('race')}
            onChange={(e) => {
              setSelectedRace(e.target.value);
            }}
          >
            <option value="" selected disabled>
              - - - -
            </option>
            {allRaces &&
              allRaces.map((race) => (
                <option key={race.index}>{race.name}</option>
              ))}
          </StyledSelect>
          {/* errors will return when field validation fails  */}
          {errors.raceRequired && <span>This field is required</span>}
        </StyledForm>
      </StyledStepsContainer>
    </StyledCharacterBuilderContainer>
  );
};
