import * as React from 'react';
import styled from 'styled-components';
import {
  AbilityOptions,
  AbilityOptionsFormNames,
  AbilityOptionsFull,
  GenericComponentProps,
} from '../../types';
import {
  APP_BORDER_RADIUS,
  StyledContainer,
  StyledStepsHeader,
  StyledStepsSubheader,
  StyledRandomiseButton,
  StyledStepContainer,
  StyledButtonContainer,
  StyledStepButton,
} from '../styles';
import { getRandomInt } from '../../helpers/randomise';

export const MIN_SCORE = 5;
export const MAX_SCORE = 18;

const StyledScoresContainer = styled.div`
  display: flex;
  margin-top: 32px;
  @media only screen and (max-width: 480px) {
    flex-direction: column;
  }
`;

const StyledInput = styled.input`
  width: 40px;
  height: 25px;
  border-radius: ${APP_BORDER_RADIUS};
  border: 1px solid threedface;
`;

const StyledLabel = styled.label`
  padding: 0 12px;
`;

const StyledFieldContainer = styled.fieldset`
  display: flex;
  align-items: center;
  margin: 0 8px;
  border-style: solid;
  border-radius: ${APP_BORDER_RADIUS};
  @media only screen and (max-width: 480px) {
    margin: 8px 0;
    justify-content: space-between;
    max-width: 185px;
  }
`;

export const AbilityScores: React.FC<GenericComponentProps> = (props) => {
  const [abilityScores, setAbilityScores] = React.useState<
    AbilityOptions | undefined
  >(undefined);

  const handleScoreUpdate = (
    score: string | number,
    stateName: AbilityOptionsFull,
    fieldName: AbilityOptionsFormNames
  ) => {
    const scoreAsNumber =
      typeof score === 'number' ? score : parseInt(score, 10);
    props.setFieldValue(fieldName, scoreAsNumber);
    setAbilityScores({
      [stateName]: scoreAsNumber,
    });
  };

  const handleRandomScores = () => {
    const randomStrength = getRandomInt(MAX_SCORE, MIN_SCORE);
    handleScoreUpdate(randomStrength, 'strength', 'abilityScores.strength');

    const randomDexterity = getRandomInt(MAX_SCORE, MIN_SCORE);
    handleScoreUpdate(randomDexterity, 'dexterity', 'abilityScores.dexterity');

    const randomConstitution = getRandomInt(MAX_SCORE, MIN_SCORE);
    handleScoreUpdate(
      randomConstitution,
      'constitution',
      'abilityScores.constitution'
    );

    const randomIntelligence = getRandomInt(MAX_SCORE, MIN_SCORE);
    handleScoreUpdate(
      randomIntelligence,
      'intelligence',
      'abilityScores.intelligence'
    );

    const randomWisdom = getRandomInt(MAX_SCORE, MIN_SCORE);
    handleScoreUpdate(randomWisdom, 'wisdom', 'abilityScores.wisdom');

    const randomCharisma = getRandomInt(MAX_SCORE, MIN_SCORE);
    handleScoreUpdate(randomCharisma, 'charisma', 'abilityScores.charisma');
  };

  return (
    <StyledStepContainer>
      <StyledContainer>
        <StyledStepsHeader>Step Three: Ability Scores</StyledStepsHeader>
        <StyledStepsSubheader>
          Enter your ability scores or{' '}
          <StyledRandomiseButton onClick={() => handleRandomScores()}>
            randomise
          </StyledRandomiseButton>{' '}
          them.
        </StyledStepsSubheader>
        <StyledScoresContainer>
          <StyledFieldContainer>
            <StyledLabel>Strength</StyledLabel>
            <StyledInput
              defaultValue={abilityScores?.strength}
              type="number"
              {...props.register('abilityScores.strength')}
              onChange={(e) => {
                handleScoreUpdate(
                  e.target.value,
                  'strength',
                  'abilityScores.strength'
                );
              }}
            />
            {props.errors.abilityScores?.strength && (
              <span>Please select a number between 3 and 18</span>
            )}
          </StyledFieldContainer>

          <StyledFieldContainer>
            <StyledLabel>Dexterity</StyledLabel>
            <StyledInput
              defaultValue={abilityScores?.dexterity}
              type="number"
              {...props.register('abilityScores.dexterity')}
              onChange={(e) => {
                handleScoreUpdate(
                  e.target.value,
                  'dexterity',
                  'abilityScores.dexterity'
                );
              }}
            />
            {props.errors.abilityScores?.dexterity && (
              <span>Please select a number between 3 and 18</span>
            )}
          </StyledFieldContainer>

          <StyledFieldContainer>
            <StyledLabel>Constitution</StyledLabel>
            <StyledInput
              defaultValue={abilityScores?.constitution}
              type="number"
              {...props.register('abilityScores.constitution')}
              onChange={(e) => {
                handleScoreUpdate(
                  e.target.value,
                  'constitution',
                  'abilityScores.constitution'
                );
              }}
            />
            {props.errors.abilityScores?.constitution && (
              <span>Please select a number between 3 and 18</span>
            )}
          </StyledFieldContainer>

          <StyledFieldContainer>
            <StyledLabel>Intelligence</StyledLabel>
            <StyledInput
              defaultValue={abilityScores?.intelligence}
              type="number"
              {...props.register('abilityScores.intelligence')}
              onChange={(e) => {
                handleScoreUpdate(
                  e.target.value,
                  'intelligence',
                  'abilityScores.intelligence'
                );
              }}
            />
            {props.errors.abilityScores?.intelligence && (
              <span>Please select a number between 3 and 18</span>
            )}
          </StyledFieldContainer>

          <StyledFieldContainer>
            <StyledLabel>Wisdom</StyledLabel>
            <StyledInput
              defaultValue={abilityScores?.wisdom}
              type="number"
              {...props.register('abilityScores.wisdom')}
              onChange={(e) => {
                handleScoreUpdate(
                  e.target.value,
                  'wisdom',
                  'abilityScores.wisdom'
                );
              }}
            />
            {props.errors.abilityScores?.wisdom && (
              <span>Please select a number between 3 and 18</span>
            )}
          </StyledFieldContainer>

          <StyledFieldContainer>
            <StyledLabel>Charisma</StyledLabel>
            <StyledInput
              defaultValue={abilityScores?.charisma}
              type="number"
              {...props.register('abilityScores.charisma')}
              onChange={(e) => {
                handleScoreUpdate(
                  e.target.value,
                  'charisma',
                  'abilityScores.charisma'
                );
              }}
            />
            {props.errors.abilityScores?.charisma && (
              <span>Please select a number between 3 and 18</span>
            )}
          </StyledFieldContainer>
        </StyledScoresContainer>
        <StyledButtonContainer>
          <StyledStepButton onClick={props.handleStepBack}>
            &#8592; Previous: Class
          </StyledStepButton>
          <StyledStepButton
            onClick={props.handleStepForward}
            disabled={
              !abilityScores ||
              !abilityScores?.strength ||
              !abilityScores?.charisma ||
              !abilityScores?.intelligence ||
              !abilityScores?.wisdom ||
              !abilityScores?.dexterity ||
              !abilityScores?.constitution
            }
          >
            Next: Background &#8594;
          </StyledStepButton>
        </StyledButtonContainer>
      </StyledContainer>
    </StyledStepContainer>
  );
};
