import * as React from 'react';
import styled from 'styled-components';
import { DeepMap, FieldError, UseFormRegister } from 'react-hook-form';
import {
  AbilityMapEnum,
  APIReference,
  CommonModel,
  FormInputs,
} from '../../types';
import {
  StyledContainer,
  StyledStepsHeader,
  StyledDetailHeader,
  StyledStepsSubheader,
  StyledRandomiseButton,
  StyledStepContainer,
  StyledDetails,
  StyledP,
  StyledList,
  StyledListItem,
  StyledButtonContainer,
  StyledStepButton,
} from '../styles';

interface AbilityScoresProps {
  register: UseFormRegister<FormInputs>;
  errors: DeepMap<FormInputs, FieldError>;
  handleStepForward: () => void;
  handleStepBack: () => void;
}

const StyledInput = styled.input``;
const StyledLabel = styled.label``;
const StyledFieldContainer = styled.fieldset``;

export const AbilityScores: React.FC<AbilityScoresProps> = (props) => {
  const [selectedRace, setSelectedRace] = React.useState<
    APIReference | undefined
  >(undefined);

  const handleRandomScores = () => {};

  const handleScoreUpdate = (score: string) => {};

  return (
    <StyledStepContainer>
      <StyledContainer>
        <StyledStepsHeader>Step Three: Ability Scores</StyledStepsHeader>
        <StyledStepsSubheader>
          Enter your ability scores or{' '}
          <StyledRandomiseButton onClick={() => handleRandomScores()}>
            randomise
          </StyledRandomiseButton>{' '}
          it.
        </StyledStepsSubheader>
        <StyledFieldContainer>
          <StyledLabel>Strength</StyledLabel>
          <StyledInput
            value={selectedRace?.index}
            {...props.register('abilityScores.strength')}
            onChange={(e) => {
              handleScoreUpdate(e.target.value);
            }}
          />
          {props.errors.race && <span>This field is required</span>}
        </StyledFieldContainer>
      </StyledContainer>
    </StyledStepContainer>
  );
};
