import * as React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styled from 'styled-components';
import { TEXT_COLOR_PRIMARY, TEXT_COLOR_SECONDARY } from '../styles';
import { Race } from '../components/race';
import { Class } from '../components/class';
import {
  AbilityOptions,
  ClassEnum,
  Classes,
  FormInputs,
  RaceEnum,
  Races,
} from '../../types';
import { AbilityScores } from '../components/abilityScores';
import { Background } from '../components/background';

const StyledCharacterBuilderContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 100%;
  color: ${TEXT_COLOR_PRIMARY};
  margin-top: 32px;
`;

const StyledCharacterBuilderTitle = styled.h1`
  text-align: center;
`;

const StyledCharacterBuilderTitleContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin-bottom: 32px;
`;

const StyledStepsContainer = styled.div``;

const StyledRandomiseButton = styled.a`
  font-size: inherit;
  color: ${TEXT_COLOR_SECONDARY};
  &:hover {
    cursor: pointer;
    color: ${TEXT_COLOR_PRIMARY};
  }
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
`;

const StyledP = styled.p`
  margin: 12px 0;
`;

const StyledSelectedOptions = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

const StyledSelectedAbilityOptions = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

export const CharacterBuilder: React.FC = () => {
  const {
    register,
    handleSubmit,
    formState: { errors },
    getValues,
    setValue,
  } = useForm<FormInputs>({
    defaultValues: {
      race: '',
      class: '',
    },
  });
  const onSubmit: SubmitHandler<FormInputs> = (data) => console.log(data);
  const selectedRace = getValues('race') as Races;
  const selectedClass = getValues('class') as Classes;
  const selectedAbilities = getValues('abilityScores') as AbilityOptions;
  const [stepNum, setStepNum] = React.useState(1);

  return (
    <StyledCharacterBuilderContainer>
      <StyledCharacterBuilderTitleContainer>
        <StyledCharacterBuilderTitle>
          Character builder
        </StyledCharacterBuilderTitle>
        <StyledP>
          Build your character step by step or completely{' '}
          <StyledRandomiseButton>randomise</StyledRandomiseButton>.
        </StyledP>
        <StyledSelectedOptions>
          {selectedRace !== '' && `Race: ${RaceEnum[selectedRace]}`}{' '}
          {selectedClass !== '' && `| Class: ${ClassEnum[selectedClass]}`}
          {selectedAbilities && <>STR: {selectedAbilities.strength}</>}
        </StyledSelectedOptions>
        <StyledSelectedOptions></StyledSelectedOptions>
      </StyledCharacterBuilderTitleContainer>
      <StyledStepsContainer>
        <StyledForm onSubmit={handleSubmit(onSubmit)}>
          {stepNum === 1 && (
            <Race
              register={register}
              errors={errors}
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
              setFieldValue={setValue}
            />
          )}
          {stepNum === 2 && (
            <Class
              register={register}
              errors={errors}
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
              setFieldValue={setValue}
            />
          )}
          {stepNum === 3 && (
            <AbilityScores
              register={register}
              errors={errors}
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
              setFieldValue={setValue}
            />
          )}
          {stepNum === 4 && (
            <Background
              register={register}
              errors={errors}
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
              setFieldValue={setValue}
            />
          )}
        </StyledForm>
      </StyledStepsContainer>
    </StyledCharacterBuilderContainer>
  );
};
