import * as React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styled from 'styled-components';
import { useAppDispatch } from '../../helpers/hooks';
import { commonActions } from '../../store/slices/common';
import {
  useAllRaces,
  useDetailedRace,
  useAllSubRaces,
  useAllClasses,
  useAllSubClasses,
} from '../../store/selectors/common';
import { TEXT_COLOR_PRIMARY, TEXT_COLOR_SECONDARY } from '../styles';
import { CommonModel } from '../../types';

const StyledCharacterBuilderContainer = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: 100%;
  color: ${TEXT_COLOR_PRIMARY};
`;

const StyledCharacterBuilderTitle = styled.h1`
  text-align: center;
`;

const StyledCharacterBuilderTitleContainer = styled.div`
  text-align: center;
  margin-bottom: 32px;
`;

const StyledStepsContainer = styled.div``;

const StyledStep = styled.div`
  text-align: center;
`;

const StyledStepsHeader = styled.h3`
  text-align: center;
  margin: 12px 0;
`;

const StyledStepsSubheader = styled.h5`
  text-align: center;
  margin: 12px 0;
`;

const StyledSelect = styled.select`
  width: 200px;
  height: 30px;
  border-radius: 5px;
  margin: 12px 0;
`;

const StyledRandomiseButton = styled.a`
  font-size: inherit;
  color: ${TEXT_COLOR_SECONDARY};
  &:hover {
    cursor: pointer;
    color: ${TEXT_COLOR_PRIMARY};
  }
`;

const StyledForm = styled.form`
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const StyledP = styled.p`
  margin: 12px 0;
`;

interface FormInputs {
  race: string;
  subRace: string;
  class: string;
  subClass: string;
}

interface APIReference {
  index: string;
  name: string;
}

export const CharacterBuilder: React.FC = () => {
  const dispatch = useAppDispatch();
  const [displayRandom, setDisplayRandom] = React.useState(false);
  const allRaces = useAllRaces();
  const detailedRace = useDetailedRace();
  const allSubRaces = useAllSubRaces();
  const allClasses = useAllClasses();
  const allSubClasses = useAllSubClasses();
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm<FormInputs>();
  const onSubmit: SubmitHandler<FormInputs> = (data) => console.log(data);
  const [selectedRace, setSelectedRace] = React.useState<
    APIReference | undefined
  >(undefined);
  const [selectedSubRace, setSelectedSubRace] = React.useState<
    APIReference | undefined
  >(undefined);
  const [selectedClass, setSelectedClass] = React.useState<
    APIReference | undefined
  >(undefined);
  const [selectedSubClass, setSelectedSubClass] = React.useState<
    APIReference | undefined
  >(undefined);

  const getRandomInt = (maxInt: number) => {
    return Math.floor(Math.random() * maxInt);
  };

  const handleRandomise = (arr: Array<CommonModel>) => {
    const randomInt = getRandomInt(arr.length);
    return arr[randomInt];
  };

  const handleRandomRace = () => {
    const randomRace = handleRandomise(allRaces?.results);
    setSelectedRace({ index: randomRace.index, name: randomRace.name });
  };

  const handleSelectedRace = (index: string) => {
    const selectedRace = allRaces?.results.filter(
      (race) => race.index === index
    );
    if (selectedRace.length > 0) {
      setSelectedRace({
        index: selectedRace[0].index,
        name: selectedRace[0].name,
      });
    }
  };

  const handleSelectedSubRace = (index: string) => {
    const selectedSubRace = allSubRaces?.results.filter(
      (subRace) => subRace.index === index
    );
    if (selectedSubRace.length > 0) {
      setSelectedSubRace({
        index: selectedSubRace[0].index,
        name: selectedSubRace[0].name,
      });
    }
  };

  const handleSelectedClass = (index: string) => {
    const selectedClass = allClasses?.results.filter(
      (item) => item.index === index
    );
    if (selectedClass.length > 0) {
      setSelectedClass({
        index: selectedClass[0].index,
        name: selectedClass[0].name,
      });
    }
  };

  const handleSelectedSubClass = (index: string) => {
    const selectedSubClass = allSubClasses?.results.filter(
      (item) => item.index === index
    );
    if (selectedSubClass.length > 0) {
      setSelectedSubClass({
        index: selectedSubClass[0].index,
        name: selectedSubClass[0].name,
      });
    }
  };

  React.useEffect(() => {
    dispatch(commonActions.getAllRaces());
    if (selectedRace) {
      dispatch(commonActions.getRaceDetails({ index: selectedRace.index }));
    }
    console.log(detailedRace);
  }, [selectedRace]);

  return (
    <StyledCharacterBuilderContainer>
      <StyledCharacterBuilderTitleContainer>
        <StyledCharacterBuilderTitle>
          Character builder
        </StyledCharacterBuilderTitle>
        <StyledP>
          Build your character step by step or completely{' '}
          <StyledRandomiseButton>randomise</StyledRandomiseButton>.
        </StyledP>
      </StyledCharacterBuilderTitleContainer>
      {!displayRandom ? (
        <StyledStepsContainer>
          <StyledForm onSubmit={handleSubmit(onSubmit)}>
            {/* Select a race */}
            <StyledStep>
              <StyledStepsHeader>Step One: Race</StyledStepsHeader>
              <StyledStepsSubheader>
                Select a race or{' '}
                <StyledRandomiseButton onClick={() => handleRandomRace()}>
                  randomise
                </StyledRandomiseButton>{' '}
                it.
              </StyledStepsSubheader>
              <StyledSelect
                value={selectedRace?.name}
                {...register('race')}
                onChange={(e) => {
                  handleSelectedRace(e.target.value);
                }}
              >
                <option value="" selected disabled>
                  - - - -
                </option>
                {allRaces &&
                  allRaces.results.map((race) => (
                    <option key={race.index} value={race.index}>
                      {race.name}
                    </option>
                  ))}
              </StyledSelect>

              {errors.race && <span>This field is required</span>}
            </StyledStep>
            {/* Select a subrace */}
            {allSubRaces && allSubRaces.results.length > 0 && (
              <StyledStep>
                <StyledStepsSubheader>
                  Select a subrace or{' '}
                  <StyledRandomiseButton onClick={() => handleRandomRace()}>
                    randomise
                  </StyledRandomiseButton>{' '}
                  it.
                </StyledStepsSubheader>
                <StyledSelect
                  value={selectedSubRace?.name}
                  {...register('subRace')}
                  onChange={(e) => {
                    handleSelectedSubRace(e.target.value);
                  }}
                >
                  <option value="" selected disabled>
                    - - - -
                  </option>
                  {allSubRaces &&
                    allSubRaces.results.map((subRace) => (
                      <option key={subRace.index} value={subRace.index}>
                        {subRace.name}
                      </option>
                    ))}
                </StyledSelect>

                {errors.subRace && <span>This field is required</span>}
              </StyledStep>
            )}
            {/* Select a class */}
            {allClasses && allClasses.results.length > 0 && (
              <StyledStep>
                <StyledStepsHeader>Step Two: Class</StyledStepsHeader>
                <StyledStepsSubheader>
                  Select a class or{' '}
                  <StyledRandomiseButton onClick={() => handleRandomRace()}>
                    randomise
                  </StyledRandomiseButton>{' '}
                  it.
                </StyledStepsSubheader>
                <StyledSelect
                  value={selectedClass?.name}
                  {...register('class')}
                  onChange={(e) => {
                    handleSelectedClass(e.target.value);
                  }}
                >
                  <option value="" selected disabled>
                    - - - -
                  </option>
                  {allClasses &&
                    allClasses.results.map((item) => (
                      <option key={item.index} value={item.index}>
                        {item.name}
                      </option>
                    ))}
                </StyledSelect>

                {errors.class && <span>This field is required</span>}
              </StyledStep>
            )}
            {/* Select a subclass */}
            {allSubClasses && allSubClasses.results.length > 0 && (
              <StyledStep>
                <StyledStepsSubheader>
                  Select a subclass or{' '}
                  <StyledRandomiseButton onClick={() => handleRandomRace()}>
                    randomise
                  </StyledRandomiseButton>{' '}
                  it.
                </StyledStepsSubheader>
                <StyledSelect
                  value={selectedSubClass?.name}
                  {...register('subClass')}
                  onChange={(e) => {
                    handleSelectedSubClass(e.target.value);
                  }}
                >
                  <option value="" selected disabled>
                    - - - -
                  </option>
                  {allSubClasses &&
                    allSubClasses.results.map((item) => (
                      <option key={item.index} value={item.index}>
                        {item.name}
                      </option>
                    ))}
                </StyledSelect>

                {errors.class && <span>This field is required</span>}
              </StyledStep>
            )}
            {/* Set ability scores */}
            {/* Select a background */}
          </StyledForm>
        </StyledStepsContainer>
      ) : (
        <h2>Random</h2>
      )}
    </StyledCharacterBuilderContainer>
  );
};
