import axios, { AxiosInstance, AxiosResponse } from 'axios';
import { APIRacesResponse } from './types';

export const ApiAxiosInstance: AxiosInstance = axios.create({
  baseURL: "https://www.dnd5eapi.co/api/",
  validateStatus: () => true,
});

ApiAxiosInstance.interceptors.response.use(
  (response) => response,
  (error) => {
    return Promise.resolve(error);
  },
);

export const getRace = (index?: string) => {
  return ApiAxiosInstance.get(`races${index ? `/${index}` : ''}`)
    .then((success: AxiosResponse<APIRacesResponse>) => success)
    .catch((reason: AxiosResponse) => reason);
};

export const getSubRace = (index?: string) => {
  return ApiAxiosInstance.get(`subraces${index ? `/${index}` : ''}`)
    .then((success: AxiosResponse) => success)
    .catch((reason: AxiosResponse) => reason);
};

export const getClass = (index?: string) => {
  return ApiAxiosInstance.get(`classes${index ? `/${index}` : ''}`)
    .then((success: AxiosResponse) => success)
    .catch((reason: AxiosResponse) => reason);
};

export const getSubClass = (index?: string) => {
  return ApiAxiosInstance.get(`subclasses${index ? `/${index}` : ''}`)
    .then((success: AxiosResponse) => success)
    .catch((reason: AxiosResponse) => reason);
};

export const getAllRaces = () => {
  return ApiAxiosInstance.get('races')
    .then((success: AxiosResponse<APIRacesResponse>) => success)
    .catch((reason: AxiosResponse) => reason);
};

export const getAllSubRaces = () => {
  return ApiAxiosInstance.get('subraces')
    .then((success: AxiosResponse) => success)
    .catch((reason: AxiosResponse) => reason);
};

export const getAllClasses = () => {
  return ApiAxiosInstance.get('classes')
    .then((success: AxiosResponse) => success)
    .catch((reason: AxiosResponse) => reason);
};

export const getAllSubClasses = () => {
  return ApiAxiosInstance.get('subclasses')
    .then((success: AxiosResponse) => success)
    .catch((reason: AxiosResponse) => reason);
};
