import { takeEvery } from 'redux-saga/effects';
import { commonActions } from '../slices/common';
import { characterBuilderActions } from '../slices/characterBuilder';
import { glossaryActions } from '../slices/glossary';

import { returnAllRaces } from '../sagas/common';
import { getCharacter } from '../sagas/characterBuilderGenerator';
import { getTerm } from '../sagas/glossaryGenerator';

export function* rootSaga() {
  yield takeEvery(commonActions.getAllRaces, returnAllRaces);
}
