import * as React from 'react';
import { useFormik, Form, FormikProps } from 'formik';
import styled from 'styled-components';
import { TEXT_COLOR_PRIMARY, TEXT_COLOR_SECONDARY } from '../styles';
import { Race } from '../components/race';
import { Class } from '../components/class';
import {
  AbilityOptions,
  ClassEnum,
  Classes,
  FormInputs,
  RaceEnum,
  Races,
} from '../../types';
import { AbilityScores } from '../components/abilityScores';
import { Background } from '../components/background';

const StyledCharacterBuilderContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  height: 100%;
  color: ${TEXT_COLOR_PRIMARY};
  margin-top: 32px;
`;

const StyledCharacterBuilderTitle = styled.h1`
  text-align: center;
`;

const StyledCharacterBuilderTitleContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  margin-bottom: 32px;
`;

const StyledStepsContainer = styled.div``;

const StyledRandomiseButton = styled.a`
  font-size: inherit;
  color: ${TEXT_COLOR_SECONDARY};
  &:hover {
    cursor: pointer;
    color: ${TEXT_COLOR_PRIMARY};
  }
`;

const StyledForm = styled(Form)`
  display: flex;
  flex-direction: column;
`;

const StyledP = styled.p`
  margin: 12px 0;
`;

const StyledSelectedOptions = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`;

export const CharacterBuilder: React.FC = () => {
  const [stepNum, setStepNum] = React.useState(1);
  const formik = useFormik<FormInputs>({
    initialValues: {
      race: '',
      subRace: '',
      class: '',
      subClass: '',
    },
    onSubmit: (values) => {
      alert(JSON.stringify(values, null, 2));
    },
  });
  return (
    <StyledCharacterBuilderContainer>
      <StyledCharacterBuilderTitleContainer>
        <StyledCharacterBuilderTitle>
          Character builder
        </StyledCharacterBuilderTitle>
        <StyledP>
          Build your character step by step or completely{' '}
          <StyledRandomiseButton>randomise</StyledRandomiseButton>.
        </StyledP>
      </StyledCharacterBuilderTitleContainer>
      <StyledSelectedOptions>
        {formik.values.race !== '' && (
          <span>
            <strong>Race</strong>: {RaceEnum[formik.values.race]}
          </span>
        )}
        {formik.values.class !== '' && (
          <span>
            | <strong>Class</strong>: {ClassEnum[formik.values.class]}
          </span>
        )}
      </StyledSelectedOptions>
      <StyledSelectedOptions>
        {formik.values.abilityScores?.strength && (
          <span>
            <strong>STR</strong>: {formik.values.abilityScores.strength}
          </span>
        )}
        {formik.values.abilityScores?.dexterity && (
          <span>
            <strong>DEX</strong>: {formik.values.abilityScores.dexterity}
          </span>
        )}
        {formik.values.abilityScores?.constitution && (
          <span>
            <strong>CON</strong>: {formik.values.abilityScores.constitution}
          </span>
        )}
        {formik.values.abilityScores?.intelligence && (
          <span>
            <strong>INT</strong>: {formik.values.abilityScores.intelligence}
          </span>
        )}
        {formik.values.abilityScores?.wisdom && (
          <span>
            <strong>WIS</strong>: {formik.values.abilityScores.wisdom}
          </span>
        )}
        {formik.values.abilityScores?.charisma && (
          <span>
            <strong>CHA</strong>: {formik.values.abilityScores.charisma}
          </span>
        )}
      </StyledSelectedOptions>
      <StyledStepsContainer>
        <StyledForm onSubmit={formik.handleSubmit}>
          {stepNum === 1 && (
            <Race
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
            />
          )}
          {stepNum === 2 && (
            <Class
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
            />
          )}
          {stepNum === 3 && (
            <AbilityScores
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
            />
          )}
          {stepNum === 4 && (
            <Background
              handleStepForward={() => setStepNum(stepNum + 1)}
              handleStepBack={() => setStepNum(stepNum - 1)}
            />
          )}
        </StyledForm>
      </StyledStepsContainer>
    </StyledCharacterBuilderContainer>
  );
};
