import { createAction, createSlice, PayloadAction } from '@reduxjs/toolkit';

// set selected race
// set selected subrace
// set selected class
// set selected subclass
// set ability scores { each individual one }
// set selected background
// save form


export interface CharacterBuilderState {
  selectedRace: {
    selected?: string;
    pending?: boolean;
    success?: boolean;
    failure?: boolean;
  };
  selectedSubrace: {
    selected?: string;
    pending?: boolean;
    success?: boolean;
    failure?: boolean;
  };
  selectedClass: {
    selected?: string;
    pending?: boolean;
    success?: boolean;
    failure?: boolean;
  };
  selectedSubclass: {
    selected?: string;
    pending?: boolean;
    success?: boolean;
    failure?: boolean;
  };
}

const initialState: CharacterBuilderState = {
  selectedRace: {},
  selectedSubrace: {},
  selectedClass: {},
  selectedSubclass: {},
};

const SLICE_NAME = 'characterBuilder';

const characterBuilderSlice = createSlice({
  name: SLICE_NAME,
  initialState,
  reducers: {
    setSelectedRaceToStatePending: (state, action: PayloadAction<string>) => {
      state.selectedRace.selected = action.payload;
    },
    setSelectedRaceToStateSuccess: (state, action: PayloadAction<string>) => {
      state.selectedRace = action.payload;
    },
    setSelectedRaceToStateFailure: (state, action: PayloadAction<string>) => {
      state.selectedRace = action.payload;
    },
    setSelectedSubraceToState: (state, action: PayloadAction<string>) => {
      state.selectedSubrace = action.payload;
    },
    setSelectedClassToState: (state, action: PayloadAction<string>) => {
      state.selectedClass = action.payload;
    },
    setSelectedSubclassToState: (state, action: PayloadAction<string>) => {
      state.selectedSubclass = action.payload;
    },
  },
});

export const { reducer: characterBuilderReducer } = characterBuilderSlice;

export const characterBuilderActions = {
  ...characterBuilderSlice.actions,

  setSelectedRaceToState: createAction(
    `${SLICE_NAME}/setSelectedRaceToState`,
    (payload: { race: string }) => ({
      payload,
    }),
  ),
  setSelectedSubraceToState: createAction(
    `${SLICE_NAME}/setSelectedSubraceToState`,
    (payload: { subrace: string }) => ({
      payload,
    }),
  ),
  setSelectedClassToState: createAction(
    `${SLICE_NAME}/setSelectedClassToState`,
    (payload: { class: string }) => ({
      payload,
    }),
  ),
  setSelectedSubclassToState: createAction(
    `${SLICE_NAME}/setSelectedSubclassToState`,
    (payload: { subclass: string }) => ({
      payload,
    }),
  ),
};
